const Forms = document.querySelectorAll('.simple_form')
const Button = document.getElementById('button_multi_form')

function submitForms() {
  Forms.forEach(form =>
    form.submit()
  );
}
console.log(Button)

function clickButton() {
  Button.addEventListener('click', submitForms)
}

export { clickButton }
